//*************************************************** 
// Ajax request when user changes a visibility 
//***************************************************
function updateVisibilityValue(field){
  var macroPrefix = "macrosmanager_visib_";
  var macroId = field.id.substr(macroPrefix.length);
  var notif = new XWiki.widgets.Notification("Saving...", "inprogress", {inactive: true} );
  new Ajax.Request('../MacrosManager/Ajax?xpage=plain',
                   {
                     method: 'post',
                     parameters: {macroId: macroId, visibility: field.value},
                     onLoading: function(){
                       notif.show();
                     },
                     onSuccess: function(transport){
                       notif.replace(new XWiki.widgets.Notification("Saved", "done"));
                     },
                     onFailure: function(){
                       notifSaving.hide();
                       notif.replace(new XWiki.widgets.Notification("Error, the modification can not be saved!", "error"));
                     }
                   }
                  );
}

//*************************************************** 
// Init ajax event handlers on visibility fields
//***************************************************
function initHandlers(){
  var elems = getElementsByClassName("select", "macrosmanager_select");
  for(var i=0; i<elems.length; ++i){
    var elem = elems[i];
    if(elem.hasMacroTableListener)
      return;
    Event.observe(elem.id, 'change', function(event) {
      updateVisibilityValue(this);
    });
    elem.hasMacroTableListener=true;
  }
}

//*************************************************** 
// Event when the user clicks on the livetable
//***************************************************
Event.observe('macrosTable', 'click', function(event) {
  initHandlers();
});

//*************************************************** 
// Event when the user changes the livetable
//***************************************************
Event.observe('macrosTable', 'change', function(event) {
  initHandlers();
});

//*************************************************** 
// Utility function which get all the elements which have a certain class 
//***************************************************
function getElementsByClassName(tag, name) {
  var arr = new Array();  
  var elems = document.getElementsByTagName(tag);
  for(var i=0; i<elems.length; ++i){
    var elem = elems[i];
    if(elem.className == name)
      arr[arr.length] = elem;
  }
  return arr;
}

//*************************************************** 
// Livetable update callback
//***************************************************
macrosmanager_livetable_callback = function (row, i, table) {

## === Get the visibility values ===
#set( $macroClass = $xwiki.getClass('XWiki.WikiMacroClass') )
#set( $visibilityProp = $macroClass.get('visibility') )
#set( $listValues = $visibilityProp.getListValues() )
  var visibList = [#foreach($v in $listValues)#if($velocityCount>1),#end'$v'#end];

  // Generate the visibility combo box
  var visibElem = new Element('select', {'id':'macrosmanager_visib_'+row.dbid, 'class':'macrosmanager_select'});
  for(var i=0; i<visibList.length; ++i){
    if(visibList[i]==row.visibility)
      visibElem.appendChild(new Element('option', {'selected':'selected'}).update(visibList[i]));
    else
      visibElem.appendChild(new Element('option').update(visibList[i]));
  }

  // Generate the table row
  var tr = new Element('tr');  
  tr.appendChild(new Element('td').update(row.id));
  tr.appendChild(new Element('td').update(row.name));
  tr.appendChild(new Element('td').update(row.description));
  tr.appendChild(new Element('td').update(visibElem));
  tr.appendChild(new Element('td').update(new Element('a', { 'href':row.link, 'class':'action actionedit' }).update('Edit')));
  return tr;

}
